define('scripts/views/validation/cookie.validation.amd',[
	//NO EXPORTS goes last
], function() {
	cookie = {
		//c_name : name of cookie
		//returns value or ""
		read: function(c_name) {
			var value = this.check(c_name);
			if (value != null && value != "")
				return value;
			else
				return "";
		},
		//c_name : name of cookie
		//value : the value to store
		//exdays : expire date [int]
		create: function(c_name, value, exdays) {
			var exdate = new Date();
			exdate.setDate(exdate.getDate() + exdays);
			var c_value = escape(value) + ((exdays == null) ? "" : "; expires=" + exdate.toUTCString());
			document.cookie = c_name + "=" + c_value + "; path=/";
		},
		//c_name : name of cookie
		//returns value or null
		check: function(c_name) {
			var nameEQ = c_name + "=";
			var ca = document.cookie.split(';');
			for (var i = 0; i < ca.length; i++) {
				var c = ca[i];
				while (c.charAt(0) == ' ') c = c.substring(1, c.length);
				if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
			}
			return null;
		},
		//c_name : name of cookie
		"delete": function(c_name) {
			this.create(c_name, "", -1);
		}
	};
	return cookie;
});
